pragma solidity ^0.8.20;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableMap.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

import "./ScenarioVerifier.sol";
import "../interfaces/INexeraVerifierEntrypoint.sol";

/// @title Nexera Verifier Entrypoint
/// @notice This contract manages the registration, update, and verification of Scenario Verifiers for a single project
contract NexeraVerifierEntrypoint is
    Initializable,
    INexeraVerifierEntrypoint,
    OwnableUpgradeable
{
    // Add the library methods
    using EnumerableSet for EnumerableSet.AddressSet;
    using EnumerableMap for EnumerableMap.AddressToUintMap;

    // Declare a set state variable
    EnumerableSet.AddressSet private scenarioVerifierAddresses;
    EnumerableMap.AddressToUintMap private isScenarioEnabled;

    // Events

    /// @dev Event emitted when a Scenario Verifier is added
    event ScenarioVerifierAdded(address scenarioVerifierAddress);
    /// @dev Event emitted when a Scenario Verifier is deleted
    event ScenarioVerifierDeleted(address scenarioVerifierAddress);
    /// @dev Event emitted when a Scenario Verifier is updated
    event ScenarioVerifierUpdated(
        address oldScenarioVerifierAddress,
        address newScenarioVerifierAddress
    );
    /// @dev Event emitted when a Scenario Verifier is enabled
    event ScenarioVerifierEnabled(address scenarioVerifierAddress);
    /// @dev Event emitted when a Scenario Verifier is disabled
    event ScenarioVerifierDisabled(address scenarioVerifierAddress);

    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initialize proxied contract with owner
     * @param newOwner The address of the thing owner
     */
    function initialize(address newOwner) public initializer {
        __Ownable_init(newOwner);
    }

    /// @dev Modifier to require a verified user as sender
    modifier requiresVerified() {
        require(
            isAllowedForEntrypoint(msg.sender),
            "Nexera Verifier: Sender is not verified"
        );
        _;
    }

    /// @notice Checks if a user is allowed for the entrypoint
    /// @param user The address of the user to check
    /// @return isUserAllowed True if the user is allowed, false otherwise
    function isAllowedForEntrypoint(address user) public view returns (bool) {
        bool isUserAllowed = true;
        address[] memory scenariosValues = EnumerableSet.values(
            scenarioVerifierAddresses
        );
        uint numberOfScenarios = scenariosValues.length;
        for (uint i = 0; i < numberOfScenarios; ) {
            isUserAllowed = ScenarioVerifier(scenariosValues[i])
                .isAllowedForScenario(user);
            if (!isUserAllowed) {
                break;
            }
            unchecked {
                i++;
            }
        }
        return isUserAllowed;
    }

    /// @notice Checks if a scenario verifier exists in the entrypoint
    /// @param scenarioVerifier Address of the scenario verifier to check
    /// @return exist True if the scenario verifier exists, false otherwise
    function _existScenarioVerifier(
        address scenarioVerifier
    ) internal view returns (bool) {
        return
            EnumerableSet.contains(scenarioVerifierAddresses, scenarioVerifier);
    }

    /// @notice Gets the address of a scenario verifier by index
    /// @param index The index of the verifier in the list
    /// @return Address of the scenario verifier
    function getScenarioVerifierAddress(
        uint index
    ) public view returns (address) {
        return EnumerableSet.at(scenarioVerifierAddresses, index);
    }

    /// @notice Adds a new scenario verifier to the entrypoint
    /// @param scenarioVerifierAddress Address of the new scenario verifier
    function addScenarioVerifier(
        address scenarioVerifierAddress
    ) public onlyOwner {
        require(
            scenarioVerifierAddress != address(0),
            "Input Scenario address cannot be the zero address"
        );

        bool added = EnumerableSet.add(
            scenarioVerifierAddresses,
            scenarioVerifierAddress
        );
        require(added, "Scenario Verifier already added");
        EnumerableMap.set(isScenarioEnabled, scenarioVerifierAddress, 1);

        emit ScenarioVerifierAdded(scenarioVerifierAddress);
    }

    /// @notice Deletes a scenario verifier from the entrypoint
    /// @param scenarioVerifierAddress Address of the scenario verifier to delete
    function deleteScenarioVerifier(
        address scenarioVerifierAddress
    ) public onlyOwner {
        require(
            _existScenarioVerifier(scenarioVerifierAddress),
            "Nexera Verifier: Scenario Verifier Address doesn't exist"
        );

        EnumerableSet.remove(
            scenarioVerifierAddresses,
            scenarioVerifierAddress
        );
        EnumerableMap.remove(isScenarioEnabled, scenarioVerifierAddress);

        emit ScenarioVerifierDeleted(scenarioVerifierAddress);
    }

    /// @notice Updates an existing scenario verifier
    /// @param oldScenarioVerifierAddress Address of the existing scenario verifier
    /// @param newScenarioVerifierAddress Address of the new scenario verifier
    function updateScenarioVerifier(
        address oldScenarioVerifierAddress,
        address newScenarioVerifierAddress
    ) public onlyOwner {
        require(
            _existScenarioVerifier(oldScenarioVerifierAddress),
            "Nexera Verifier: Scenario Verifier Address doesn't exist"
        );

        deleteScenarioVerifier(oldScenarioVerifierAddress);
        addScenarioVerifier(newScenarioVerifierAddress);

        emit ScenarioVerifierUpdated(
            oldScenarioVerifierAddress,
            newScenarioVerifierAddress
        );
    }

    /// @notice Enables a scenario verifier
    /// @param scenarioVerifierAddress Address of the scenario verifier to enable
    function enableScenario(address scenarioVerifierAddress) public onlyOwner {
        require(
            _existScenarioVerifier(scenarioVerifierAddress),
            "Nexera Verifier: Scenario Verifier Address doesn't exist"
        );

        EnumerableMap.set(isScenarioEnabled, scenarioVerifierAddress, 1);
        emit ScenarioVerifierEnabled(scenarioVerifierAddress);
    }

    /// @notice Disables a scenario verifier
    /// @param scenarioVerifierAddress Address of the scenario verifier to disable
    function disableScenario(address scenarioVerifierAddress) public onlyOwner {
        require(
            _existScenarioVerifier(scenarioVerifierAddress),
            "Nexera Verifier: Scenario Verifier Address doesn't exist"
        );

        EnumerableMap.set(isScenarioEnabled, scenarioVerifierAddress, 0);

        emit ScenarioVerifierDisabled(scenarioVerifierAddress);
    }

    /// @notice Gets if a scenario is enabled
    /// @param scenarioVerifierAddress Address of the scenario verifier to check
    /// @return Boolean with the response
    function getIsScenarioEnabled(
        address scenarioVerifierAddress
    ) public view returns (bool) {
        (bool check, uint256 value) = EnumerableMap.tryGet(
            isScenarioEnabled,
            scenarioVerifierAddress
        );

        return check && value == 1;
    }
}
","keccak256":"0x8232dd536cce75d3f25f433b083939a40fc5dfd9e6153784c655afc1122f44a3","license":"MIT"},"contracts/zkpVerifiers/NexeraZKPVerifier.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import {GenesisUtils} from "@iden3/contracts/lib/GenesisUtils.sol";
import {ICircuitValidator} from "@iden3/contracts/interfaces/ICircuitValidator.sol";
import {ZKPVerifier} from "@iden3/contracts/verifiers/ZKPVerifier.sol";

/// @title NexeraZKPVerifier
/// @notice Extends ZKPVerifier to handle Zero-Knowledge Proof (ZKP) requests specifically for the Nexera system.
/// @dev We use this because we need _afterSetRequest to register the request ids
contract NexeraZKPVerifier is ZKPVerifier {
    /// @dev Emitted when a ZKP request is set.
    /// @param requestId The ID of the ZKP request.
    /// @param data The data associated with the ZKP request.
    /// @param validator The circuit validator for the ZKP request.
    /// @param metadata Metadata associated with the request.
    event ZKPRequestSet(
        uint64 requestId,
        bytes data,
        ICircuitValidator validator,
        string metadata
    );

    /// @dev Internal hook that is called after a ZKP request is set.
    /// @param requestId The ID of the request that was set.
    function _afterSetRequest(uint64 requestId) internal virtual {}

    /// @notice Sets a Nexera ZKP request and emits an event.
    /// @param requestId The ID of the request to be set.
    /// @param request The ZKP request data.
    function setNexeraZKPRequest(
        uint64 requestId,
        ZKPRequest calldata request
    ) public onlyOwner {
        setZKPRequest(requestId, request);
        emit ZKPRequestSet(
            requestId,
            request.data,
            request.validator,
            request.metadata
        );
        _afterSetRequest(requestId);
    }
}
","keccak256":"0xfb4cb7d5e0e72b282d0882871cdd81ec1db51aa42827478312ace8cb632b6d87","license":"MIT"},"contracts/zkpVerifiers/ScenarioVerifier.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {PrimitiveTypeUtils} from "@iden3/contracts/lib/PrimitiveTypeUtils.sol";
import {ICircuitValidator} from "@iden3/contracts/interfaces/ICircuitValidator.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "./NexeraZKPVerifier.sol";
import "../interfaces/IScenarioVerifier.sol";

/// @title ScenarioVerifier
/// @notice This contract manages rules for a scenario using ZKP requests, including setting a request for a rule and allowing users who submit the right ZKPs
contract ScenarioVerifier is
    Initializable,
    IScenarioVerifier,
    NexeraZKPVerifier
{
    // Array of registered rule IDs
    uint64[] public registeredRuleIDs;

    // Mapping to check if a rule ID is registered
    mapping(uint64 => bool) public isRuleIdRegistered;

    // Mapping for query request whitelist status by rule ID and address
    mapping(uint64 => mapping(address => bool)) public queryRequestWhitelist;

    // Mapping for scenario whitelist status by address
    mapping(address => bool) public scenarioAllowList;

    // Mapping to connect a Polygon ID to an address
    mapping(uint256 => address) public idToAddress;

    // Mapping to connect an address to a Polygon ID
    mapping(address => uint256) public addressToId;

    // Events

    /// @dev Event emitted when a ZKPRequest is registered in the verifier
    event RequestRegistered(uint64 requestId);

    /// @dev Event emitted when an address is associated with a Polygon ID
    event AddressIdConnection(address userAddress, uint256 userId);

    /// @dev Event emitted when a user address is allowed for a request ID
    event UserAllowedForRequest(address userAddress, uint64 requestId);

    /// @dev Event emitted when all ZKPs for a user are submitted
    event SubmitedAllZKPsForUser(address userAddress, ZKP[] zkps);

    /// @dev Event emitted when an address is allowed for the scenario verifier
    event UserAllowedForScenario(address userAddress);

    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initialize proxied contract with owner
     * @param newOwner The address of the thing owner
     */
    function initialize(address newOwner) public initializer {
        _transferOwnership(newOwner);
    }

    /// @notice Internal function called after setting a request to register the request ID
    /// @param requestId The ID of the request being set
    function _afterSetRequest(uint64 requestId) internal override {
        // If requestId is not registered, register it to the list of registeredRuleIDs
        if (!isRuleIdRegistered[requestId]) {
            registeredRuleIDs.push(requestId);
            isRuleIdRegistered[requestId] = true;
            emit RequestRegistered(requestId);
        }
    }

    /// @notice Internal function called before submitting a proof, checks the sender address
    /// @param inputs Array of inputs for the proof
    /// @param validator The circuit validator
    function _beforeProofSubmit(
        uint64 /* requestId */,
        uint256[] memory inputs,
        ICircuitValidator validator
    ) internal view override {
        // check that challenge input is address of sender
        address addr = PrimitiveTypeUtils.uint256LEToAddress(
            inputs[validator.inputIndexOf("challenge")]
        );
        require(
            _msgSender() == addr,
            "address in proof is not a sender address"
        );
    }

    /// @notice Internal function called after submitting a proof, links id and address and allows user for the request/rule
    /// @param requestId The ID of the request being processed
    /// @param inputs Array of inputs for the proof
    /// @param _validator The circuit validator
    function _afterProofSubmit(
        uint64 requestId,
        uint256[] memory inputs,
        ICircuitValidator _validator
    ) internal override {
        // get user id and register it
        uint256 id = inputs[1];
        addressToId[_msgSender()] = id;
        idToAddress[id] = _msgSender();
        emit AddressIdConnection(_msgSender(), id);

        // Whitelist user for this rule
        queryRequestWhitelist[requestId][_msgSender()] = true;
        emit UserAllowedForRequest(_msgSender(), requestId);
    }

    /// @dev Once the user is allowed for all rules, call this function to finalize
    /// (this is more gas efficient than iterating over rules on every submission)
    /// @notice Finalizes the whitelisting of a user for all rules
    /// @param user The address of the user to check
    /// @return isUserWhitelisted True if the user is allowed for all rules
    function finalizeAllowListScenario(address user) public returns (bool) {
        bool isUserWhitelisted = true;
        uint numberOfRules = registeredRuleIDs.length;
        for (uint i = 0; i < numberOfRules; ) {
            isUserWhitelisted = queryRequestWhitelist[registeredRuleIDs[i]][
                user
            ];
            if (!isUserWhitelisted) {
                break;
            }
            unchecked {
                i++;
            }
        }
        scenarioAllowList[user] = isUserWhitelisted;
        emit UserAllowedForScenario(user);
        return isUserWhitelisted;
    }

    /// @notice Submits all ZKPs and allowlists a user in one call
    /// @param zkps Array of ZKPs to submit
    /// @return Boolean indicating the success of the operation
    function allowUserForScenario(ZKP[] calldata zkps) public returns (bool) {
        uint numberOfZKPs = zkps.length;
        for (uint i = 0; i < numberOfZKPs; ) {
            submitZKPResponse(
                zkps[i].requestId,
                zkps[i].inputs,
                zkps[i].a,
                zkps[i].b,
                zkps[i].c
            );
            unchecked {
                i++;
            }
        }
        emit SubmitedAllZKPsForUser(msg.sender, zkps);
        return finalizeAllowListScenario(msg.sender);
    }

    /// @notice Checks if a user is allowed for the scenario
    /// @param user The address of the user to check
    /// @return Boolean indicating if the user is allowed for the scenario
    function isAllowedForScenario(address user) public view returns (bool) {
        return scenarioAllowList[user];
    }
}
